declare(strict_types=1);

namespace MagedIn\Magento2\Framework\Module\Integration\System\Adminhtml\Source;

use Magento\Framework\Data\OptionSourceInterface;

abstract class AbstractSource implements OptionSourceInterface
{
    /**
     * @var array
     */
    private $options = [];

    /**
     * @return array
     */
    public function toOptionArray(): array
    {
        if (!$this->options) {
            foreach ($this->toOption() as $value) {
                $this->options[] = [
                    'value' => $value,
                    'label' => __($this->getOptionLabel($value)),
                ];
            }
        }
        return $this->options;
    }

    /**
     * @return array
     */
    abstract public function getOptions(): array;

    /**
     * @param string $value
     *
     * @return string
     */
    abstract public function getOptionLabel(string $value): ?string;
}
